mapper.select:
select  from live_room_info
where 1=1 
 and id=#{id}
 and room_name like concat('%',#{roomName},'%')
 and play_time between #{beginPlayTime} and #{endPlayTime}
 and play_state=#{playState}
 and room_img=#{roomImg}
 and create_user_id=#{createUserId}
 and create_time=#{createTime}
 and update_user_id=#{updateUserId}
 and update_time=#{updateTime}
 and comment=#{comment}
order by create_time

mapper.update:
update live_room_info
room_name=#{roomName}, play_time=#{playTime}, play_state=#{playState}, room_img=#{roomImg}, create_user_id=#{createUserId}, create_time=#{createTime}, update_user_id=#{updateUserId}, update_time=#{updateTime}, comment=#{comment},
where id=#{id}
