mapper.select:
select  from sys_dic_item
where 1=1
 and id=#{id}
 and dic_id=#{dicId}
 and dic_item_code like concat('%',#{dicItemCode},'%')
 and dic_item_name like concat('%',#{dicItemName},'%')
 and create_user_id=#{createUserId}
 and create_time=#{createTime}
 and update_user_id=#{updateUserId}
 and update_time=#{updateTime}
 and comment=#{comment}

mapper.update:
update sys_dic_item
dic_id=#{dicId}, dic_item_code=#{dicItemCode}, dic_item_name=#{dicItemName}, create_user_id=#{createUserId}, create_time=#{createTime}, update_user_id=#{updateUserId}, update_time=#{updateTime}, comment=#{comment},
where id=#{id}
