mapper.select:
select  from sys_log_operate
where 1=1 
 and id=#{id}
 and ip like concat('%',#{ip},'%')
 and oper_content like concat('%',#{operContent},'%')
 and task_time_span=#{taskTimeSpan}
 and create_user_id=#{createUserId}
 and create_time=#{createTime}
 and update_user_id=#{updateUserId}
 and update_time=#{updateTime}
 and comment=#{comment}
order by create_time

mapper.update:
update sys_log_operate
ip=#{ip}, oper_content=#{operContent}, task_time_span=#{taskTimeSpan}, create_user_id=#{createUserId}, create_time=#{createTime}, update_user_id=#{updateUserId}, update_time=#{updateTime}, comment=#{comment},
where id=#{id}
