mapper.select:
select

from sys_file_info
where 1=1
and id=#{id}
and fk_id=#{fkId}
and name=#{name}
and real_name=#{realName}
and file_size=#{fileSize}
and extension=#{extension}
and content_type=#{contentType}
and directory=#{directory}
and remark=#{remark}
and version=#{version}
and create_user_id=#{createUserId}
and create_time=#{createTime}
and update_user_id=#{updateUserId}
and update_time=#{updateTime}
and comment=#{comment}
order by create_time

mapper.update:
update sys_file_info
fk_id=#{fkId}, name=#{name}, real_name=#{realName}, file_size=#{fileSize}, extension=#{extension}, content_type=#{contentType}, directory=#{directory}, remark=#{remark}, version=#{version}, create_user_id=#{createUserId}, create_time=#{createTime}, update_user_id=#{updateUserId}, update_time=#{updateTime}, comment=#{comment},
where id=#{id}

update sys_file_info set fk_id=#{fkId}
where id in

   #{item}
